// frontend/src/components/VideoRecorder.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { 
  Video, 
  RadioTower, 
  Square, 
  CheckCircle2, 
  XCircle,
  AlertTriangle,
  Upload
} from 'lucide-react';

function VideoRecorder() {
  const [recording, setRecording] = useState(false);
  const [stream, setStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedBlob, setRecordedBlob] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [error, setError] = useState(null);
  const [videoName, setVideoName] = useState('');
  const [saving, setSaving] = useState(false);

  const videoRef = useRef(null);
  const chunksRef = useRef([]);
  const timerRef = useRef(null);

  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [stream]);

  const startRecording = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 1280, height: 720 }, 
        audio: true 
      });
      
      videoRef.current.srcObject = mediaStream;
      setStream(mediaStream);

      const recorder = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm'
      });

      recorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunksRef.current.push(e.data);
        }
      };

      recorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: 'video/webm' });
        setRecordedBlob(blob);
        chunksRef.current = [];
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
      setRecordingTime(0);

      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

    } catch (err) {
      setError('Failed to access camera/microphone');
      console.error(err);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      stream.getTracks().forEach(track => track.stop());
      clearInterval(timerRef.current);
      setRecording(false);
    }
  };

  const saveRecording = async () => {
    if (recordedBlob) {
      setSaving(true);
      const formData = new FormData();
      const fileName = videoName 
        ? `${videoName}.webm` 
        : `Recording-${new Date().toISOString()}.webm`;
      
      formData.append('video', recordedBlob, fileName);
      formData.append('name', fileName);

      try {
        await axios.post('http://localhost:5000/record', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        
        // Reset state
        setRecordedBlob(null);
        setVideoName('');
        setSaving(false);
        
        // Show success notification
        alert('Video saved successfully!');
      } catch (error) {
        console.error('Error saving video:', error);
        setError('Failed to save video');
        setSaving(false);
      }
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      <div className="grid md:grid-cols-2 gap-8">
        {/* Video Preview and Controls */}
        <div className="bg-white shadow-xl rounded-2xl overflow-hidden border border-gray-200">
          <div className="relative bg-gray-100">
            <video 
              ref={videoRef} 
              autoPlay 
              muted 
              className="w-full aspect-video bg-black"
              playsInline
            />
            {recording && (
              <div className="absolute top-4 right-4 bg-red-500 text-white px-3 py-1 rounded-full text-sm font-semibold flex items-center">
                <div className="w-2 h-2 bg-white rounded-full mr-2 animate-pulse"></div>
                {formatTime(recordingTime)}
              </div>
            )}
          </div>
          
          <div className="p-6">
            {error && (
              <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg flex items-center mb-4">
                <AlertTriangle className="mr-3 text-red-500" />
                {error}
              </div>
            )}

            <div className="flex space-x-4">
              {!recording ? (
                <button 
                  onClick={startRecording} 
                  className="flex-1 flex items-center justify-center bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition space-x-2"
                >
                  <RadioTower className="mr-2" /> 
                  Start Recording
                </button>
              ) : (
                <button 
                  onClick={stopRecording} 
                  className="flex-1 flex items-center justify-center bg-red-600 text-white px-6 py-3 rounded-lg hover:bg-red-700 transition space-x-2"
                >
                  <Square className="mr-2" /> 
                  Stop Recording
                </button>
              )}
            </div>
          </div>
        </div>

        {/* Recorded Video Management */}
        <div className="bg-white shadow-xl rounded-2xl overflow-hidden border border-gray-200 p-6">
          <h2 className="text-2xl font-bold mb-6 text-gray-800 flex items-center">
            <Video className="mr-3 text-blue-600" size={28} /> 
            Video Management
          </h2>

          {recordedBlob ? (
            <div className="space-y-4">
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <div className="flex items-center mb-4">
                  <CheckCircle2 className="text-green-600 mr-3" size={24} />
                  <span className="font-semibold text-gray-700">Recording Ready</span>
                </div>

                <div className="mb-4">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Video Name (Optional)
                  </label>
                  <input 
                    type="text"
                    value={videoName}
                    onChange={(e) => setVideoName(e.target.value)}
                    placeholder="Enter video name"
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <button 
                  onClick={saveRecording}
                  disabled={saving}
                  className="w-full flex items-center justify-center bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition disabled:opacity-50"
                >
                  {saving ? (
                    <>Saving...</>
                  ) : (
                    <>
                      <Upload className="mr-2" /> Save Recording
                    </>
                  )}
                </button>
              </div>
            </div>
          ) : (
            <div className="text-center py-10 text-gray-500">
              <Video size={48} className="mx-auto mb-4 text-gray-300" />
              <p>Start recording to see your video here</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default VideoRecorder;
