import React, { useState, useRef, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function VideoPlayer() {
  const videoRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const location = useLocation();
  const [videoUrl, setVideoUrl] = useState(null);

  useEffect(() => {
    const filename = location.state?.filename;
    if (filename) {
      setVideoUrl(`http://localhost:5000/video/${filename}`);
    }
  }, [location]);

  const togglePlay = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (videoRef.current) {
      videoRef.current.volume = newVolume;
    }
  };

  const handleSeek = (e) => {
    if (videoRef.current) {
      videoRef.current.currentTime = parseFloat(e.target.value);
    }
  };

  if (!videoUrl) {
    return <div className="text-center text-xl">No video selected</div>;
  }

  return (
    <div className="container mx-auto text-center">
      <h1 className="text-2xl font-bold mb-4">Video Player</h1>
      
      <div className="flex flex-col items-center">
        <video 
          ref={videoRef}
          src={videoUrl}
          className="w-full max-w-md mb-4 bg-black"
          onLoadedMetadata={() => {
            setIsPlaying(false);
          }}
        />

        <div className="flex items-center space-x-4">
          <button 
            onClick={togglePlay} 
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            {isPlaying ? 'Pause' : 'Play'}
          </button>

          <input 
            type="range" 
            min="0" 
            max={videoRef.current?.duration || 0} 
            step="0.1" 
            onChange={handleSeek}
            className="w-64"
          />

          <div className="flex items-center">
            <label htmlFor="volume" className="mr-2">Volume:</label>
            <input 
              id="volume"
              type="range" 
              min="0" 
              max="1" 
              step="0.1" 
              value={volume}
              onChange={handleVolumeChange}
              className="w-32"
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default VideoPlayer;
