import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Video, Plus, Film } from 'lucide-react';

function Dashboard() {
  const [videos, setVideos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch('http://localhost:5000/videos');
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  const handleVideoSelect = (filename) => {
    navigate('/play', { state: { filename } });
  };

  const handleNewRecording = () => {
    navigate('/record');
  };

  return (
    <div className="min-h-screen bg-black text-white p-8">
      <div className="container mx-auto max-w-6xl">
        <div className="flex justify-between items-center mb-12">
          <div className="flex items-center">
            <Film className="mr-4 text-cyan-400" size={36} />
            <h1 className="text-4xl font-bold text-cyan-300">Video Vault</h1>
          </div>
          <button 
            onClick={handleNewRecording}
            className="flex items-center bg-cyan-600 text-black px-6 py-3 rounded-full 
            hover:bg-cyan-500 transition duration-300 font-semibold shadow-lg hover:shadow-cyan-500/50"
          >
            <Plus className="mr-2" />
            New Recording
          </button>
        </div>

        {videos.length === 0 ? (
          <div className="text-center bg-gray-900 p-12 rounded-2xl border border-gray-800">
            <Video className="mx-auto mb-6 text-gray-600" size={80} />
            <p className="text-gray-400 text-xl mb-4">Your video collection is empty</p>
            <p className="text-gray-500 mb-6">Start capturing memories by clicking "New Recording"</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {videos.map((video) => (
              <div 
                key={video.filename} 
                onClick={() => handleVideoSelect(video.filename)}
                className="bg-gray-900 rounded-2xl overflow-hidden 
                cursor-pointer transform transition duration-300 
                hover:scale-105 hover:bg-gray-800 border border-gray-800 
                hover:border-cyan-600 shadow-lg hover:shadow-cyan-500/30"
              >
                <div className="p-6">
                  <div className="flex items-center justify-between mb-4">
                    <Video className="text-cyan-500 mr-3" size={32} />
                    <span className="text-sm text-gray-500">
                      {(video.size / 1024).toFixed(2)} KB
                    </span>
                  </div>
                  <h3 className="text-lg font-semibold text-cyan-300 truncate">
                    {video.filename}
                  </h3>
                  <div className="mt-2 h-1 bg-gray-700 rounded-full overflow-hidden">
                    <div className="h-full bg-cyan-600 w-1/2"></div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
