import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { 
  Video, 
  RadioTower, 
  Square, 
  CheckCircle2, 
  XCircle, 
  RefreshCcw,
  Camera
} from 'lucide-react';

function VideoRecorder() {
  const [recording, setRecording] = useState(false);
  const [stream, setStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedBlob, setRecordedBlob] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [error, setError] = useState(null);
  const [savedVideosCount, setSavedVideosCount] = useState(0);

  const videoRef = useRef(null);
  const chunksRef = useRef([]);
  const timerRef = useRef(null);

  useEffect(() => {
    // Cleanup function
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [stream]);

  const startRecording = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 1280, height: 720 }, 
        audio: true 
      });
      
      videoRef.current.srcObject = mediaStream;
      setStream(mediaStream);

      const recorder = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm'
      });

      recorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunksRef.current.push(e.data);
        }
      };

      recorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: 'video/webm' });
        setRecordedBlob(blob);
        chunksRef.current = [];
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
      setRecordingTime(0);

      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

    } catch (err) {
      setError('Failed to access camera/microphone');
      console.error(err);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      stream.getTracks().forEach(track => track.stop());
      clearInterval(timerRef.current);
      setRecording(false);
    }
  };

  const saveRecording = async () => {
    if (recordedBlob) {
      const formData = new FormData();
      formData.append('video', recordedBlob, 'recording.webm');
      formData.append('name', `Recording-${new Date().toISOString()}.webm`);

      try {
        await axios.post('http://localhost:5000/record', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        setSavedVideosCount(prev => prev + 1);
        setRecordedBlob(null);
      } catch (error) {
        console.error('Error saving video:', error);
        setError('Failed to save video');
      }
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const resetError = () => {
    setError(null);
  };

  return (
    <div className="container mx-auto px-4 py-8 bg-gray-50 min-h-screen">
      <div className="max-w-4xl mx-auto bg-white shadow-2xl rounded-2xl overflow-hidden">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Camera className="text-white" size={36} />
              <h1 className="text-3xl font-bold text-white">Video Recorder</h1>
            </div>
            {savedVideosCount > 0 && (
              <div className="bg-white/20 px-4 py-2 rounded-full text-white">
                Saved Videos: {savedVideosCount}
              </div>
            )}
          </div>
        </div>

        {/* Error Handling */}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-500 p-4 flex items-center justify-between">
            <div className="flex items-center">
              <XCircle className="text-red-500 mr-3" />
              <span className="text-red-700">{error}</span>
            </div>
            <button 
              onClick={resetError}
              className="text-red-600 hover:bg-red-100 p-2 rounded-full"
            >
              <RefreshCcw size={20} />
            </button>
          </div>
        )}

        {/* Video Preview */}
        <div className="p-6">
          <div className="bg-gray-100 border-2 border-dashed border-gray-300 rounded-2xl overflow-hidden mb-6">
            <video 
              ref={videoRef} 
              autoPlay 
              muted 
              className="w-full aspect-video object-cover"
              placeholder="Your camera preview will appear here"
            />
          </div>

          {/* Recording Controls */}
          <div className="flex flex-col items-center space-y-6">
            {/* Recording Time Display */}
            {recording && (
              <div className="bg-red-100 px-6 py-3 rounded-full">
                <span className="text-2xl font-mono font-bold text-red-600">
                  {formatTime(recordingTime)}
                </span>
              </div>
            )}

            {/* Action Buttons */}
            <div className="flex space-x-4">
              {!recording ? (
                <button 
                  onClick={startRecording} 
                  className="flex items-center bg-green-500 text-white px-8 py-3 rounded-full hover:bg-green-600 transition-colors group"
                >
                  <RadioTower className="mr-2 group-hover:animate-pulse" /> 
                  Start Recording
                </button>
              ) : (
                <button 
                  onClick={stopRecording} 
                  className="flex items-center bg-red-500 text-white px-8 py-3 rounded-full hover:bg-red-600 transition-colors"
                >
                  <Square className="mr-2" /> 
                  Stop Recording
                </button>
              )}
            </div>

            {/* Save Recording */}
            {recordedBlob && (
              <div className="w-full bg-blue-50 border border-blue-200 rounded-xl p-5 flex items-center justify-between">
                <div className="flex items-center space-x-4">
                  <CheckCircle2 className="text-green-600" size={24} />
                  <span className="text-blue-800">Recording is ready to be saved</span>
                </div>
                <button 
                  onClick={saveRecording}
                  className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                >
                  Save Recording
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default VideoRecorder;
