import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { 
  Play, 
  Pause, 
  Volume2, 
  VolumeX, 
  Maximize2, 
  Minimize2, 
  RotateCcw, 
  Download, 
  Trash2 
} from 'lucide-react';

function VideoPlayer() {
  const { videoId } = useParams();
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const [videoSrc, setVideoSrc] = useState('');
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [isMuted, setIsMuted] = useState(false);
  const [isFullScreen, setIsFullScreen] = useState(false);
  const [progress, setProgress] = useState(0);
  const [videoMetadata, setVideoMetadata] = useState(null);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        // Fetch video file
        const response = await axios.get(`http://localhost:5000/video/${videoId}`, {
          responseType: 'blob'
        });
        const videoBlob = new Blob([response.data], { type: 'video/webm' });
        const videoUrl = URL.createObjectURL(videoBlob);
        setVideoSrc(videoUrl);

        // Fetch video metadata
        const metadataResponse = await axios.get(`http://localhost:5000/video-metadata/${videoId}`);
        setVideoMetadata(metadataResponse.data);
      } catch (error) {
        console.error('Error fetching video:', error);
        navigate('/');
      }
    };

    fetchVideo();

    // Cleanup function
    return () => {
      if (videoSrc) URL.revokeObjectURL(videoSrc);
    };
  }, [videoId, navigate]);

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (videoRef.current.paused) {
        videoRef.current.play();
        setIsPlaying(true);
      } else {
        videoRef.current.pause();
        setIsPlaying(false);
      }
    }
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setVolume(newVolume);
    if (videoRef.current) {
      videoRef.current.volume = newVolume;
      setIsMuted(newVolume === 0);
    }
  };

  const toggleMute = () => {
    if (videoRef.current) {
      const newMuteState = !isMuted;
      setIsMuted(newMuteState);
      videoRef.current.muted = newMuteState;
    }
  };

  const toggleFullScreen = () => {
    if (videoRef.current) {
      if (!document.fullscreenElement) {
        if (videoRef.current.requestFullscreen) {
          videoRef.current.requestFullscreen();
        } else if (videoRef.current.mozRequestFullScreen) { // Firefox
          videoRef.current.mozRequestFullScreen();
        } else if (videoRef.current.webkitRequestFullscreen) { // Chrome, Safari and Opera
          videoRef.current.webkitRequestFullscreen();
        } else if (videoRef.current.msRequestFullscreen) { // IE/Edge
          videoRef.current.msRequestFullscreen();
        }
        setIsFullScreen(true);
      } else {
        if (document.exitFullscreen) {
          document.exitFullscreen();
        } else if (document.mozCancelFullScreen) { // Firefox
          document.mozCancelFullScreen();
        } else if (document.webkitExitFullscreen) { // Chrome, Safari and Opera
          document.webkitExitFullscreen();
        } else if (document.msExitFullscreen) { // IE/Edge
          document.msExitFullscreen();
        }
        setIsFullScreen(false);
      }
    }
  };

  const handleProgressChange = (e) => {
    const manualChange = Number(e.target.value);
    if (videoRef.current) {
      videoRef.current.currentTime = (manualChange / 100) * videoRef.current.duration;
      setProgress(manualChange);
    }
  };

  const updateProgress = () => {
    if (videoRef.current) {
      const progressPercent = 
        (videoRef.current.currentTime / videoRef.current.duration) * 100;
      setProgress(progressPercent);
    }
  };

  const handleVideoDelete = async () => {
    try {
      await axios.delete(`http://localhost:5000/video/${videoId}`);
      navigate('/');
    } catch (error) {
      console.error('Error deleting video:', error);
    }
  };

  const handleDownload = () => {
    if (videoSrc) {
      const link = document.createElement('a');
      link.href = videoSrc;
      link.download = videoMetadata?.name || 'video.webm';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };

  return (
    <div className="container mx-auto p-6 bg-gray-50 min-h-screen flex flex-col items-center">
      <div className="w-full max-w-4xl bg-white shadow-lg rounded-lg overflow-hidden">
        <div className="relative">
          <video 
            ref={videoRef}
            src={videoSrc}
            onTimeUpdate={updateProgress}
            onLoadedMetadata={() => {
              if (videoRef.current) {
                videoRef.current.volume = volume;
              }
            }}
            className="w-full"
          />
          
          {/* Video Controls */}
          <div className="absolute bottom-0 left-0 right-0 bg-black bg-opacity-50 p-4">
            <div className="flex items-center space-x-4">
              {/* Play/Pause */}
              <button onClick={togglePlayPause}>
                {isPlaying ? <Pause color="white" /> : <Play color="white" />}
              </button>

              {/* Volume */}
              <div className="flex items-center space-x-2">
                <button onClick={toggleMute}>
                  {isMuted ? <VolumeX color="white" /> : <Volume2 color="white" />}
                </button>
                <input 
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={volume}
                  onChange={handleVolumeChange}
                  className="w-24"
                />
              </div>

              {/* Progress Bar */}
              <input 
                type="range"
                min="0"
                max="100"
                value={progress}
                onChange={handleProgressChange}
                className="flex-grow"
              />

              {/* Additional Controls */}
              <div className="flex space-x-2">
                <button onClick={toggleFullScreen}>
                  {isFullScreen ? <Minimize2 color="white" /> : <Maximize2 color="white" />}
                </button>
                <button onClick={handleDownload}>
                  <Download color="white" />
                </button>
                <button onClick={handleVideoDelete}>
                  <Trash2 color="white" />
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Video Metadata */}
        {videoMetadata && (
          <div className="p-4 bg-gray-100">
            <h2 className="text-xl font-bold">{videoMetadata.name}</h2>
            <div className="flex justify-between text-gray-600">
              <span>Created: {new Date(videoMetadata.created_at).toLocaleString()}</span>
              <span>Duration: {videoMetadata.duration} seconds</span>
              <span>Size: {(videoMetadata.size / (1024 * 1024)).toFixed(2)} MB</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default VideoPlayer;
