// frontend/src/components/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { 
  Film, 
  Plus, 
  Trash2, 
  PlayCircle, 
  Calendar, 
  Clock 
} from 'lucide-react';

function Dashboard() {
  const [videos, setVideos] = useState([]);
  const [selectedVideos, setSelectedVideos] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('newest');

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get('http://localhost:5000/videos');
        const sortedVideos = response.data.sort((a, b) => 
          sortBy === 'newest' 
            ? new Date(b.created_at) - new Date(a.created_at)
            : a.name.localeCompare(b.name)
        );
        setVideos(sortedVideos);
      } catch (error) {
        console.error('Error fetching videos:', error);
      }
    };

    fetchVideos();
  }, [sortBy]);

  const handleVideoSelect = (videoId) => {
    setSelectedVideos(prev => 
      prev.includes(videoId)
        ? prev.filter(id => id !== videoId)
        : [...prev, videoId]
    );
  };

  const handleDeleteSelected = async () => {
    try {
      await Promise.all(
        selectedVideos.map(id => 
          axios.delete(`http://localhost:5000/video/${id}`)
        )
      );
      // Refresh video list
      const response = await axios.get('http://localhost:5000/videos');
      setVideos(response.data);
      setSelectedVideos([]);
    } catch (error) {
      console.error('Error deleting videos:', error);
    }
  };

  const filteredVideos = videos.filter(video => 
    video.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto p-6 bg-gray-50 min-h-screen">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800 flex items-center">
          <Film className="mr-3 text-blue-600" size={36} />
          Video Dashboard
        </h1>
        <Link 
          to="/record" 
          className="flex items-center bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
        >
          <Plus className="mr-2" /> Record New Video
        </Link>
      </div>

      <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
        <div className="flex space-x-4 mb-4">
          <input 
            type="text" 
            placeholder="Search videos..." 
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="flex-grow px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <select 
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="newest">Newest First</option>
            <option value="alphabetical">Alphabetical</option>
          </select>
          {selectedVideos.length > 0 && (
            <button 
              onClick={handleDeleteSelected}
              className="flex items-center bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition"
            >
              <Trash2 className="mr-2" /> Delete Selected
            </button>
          )}
        </div>

        {filteredVideos.length === 0 ? (
          <div className="text-center py-10 text-gray-500">
            <Film size={48} className="mx-auto mb-4 text-gray-300" />
            <p>No videos found. Start recording your first video!</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {filteredVideos.map((video) => (
              <div 
                key={video.id} 
                className="bg-white border rounded-lg shadow-md overflow-hidden hover:shadow-xl transition-shadow"
              >
                <div className="flex items-center p-4 border-b">
                  <input 
                    type="checkbox" 
                    checked={selectedVideos.includes(video.id)}
                    onChange={() => handleVideoSelect(video.id)}
                    className="mr-3"
                  />
                  <h2 className="text-lg font-semibold text-gray-800 truncate">{video.name}</h2>
                </div>
                <div className="p-4">
                  <div className="flex items-center mb-2 text-gray-600">
                    <Calendar className="mr-2 text-blue-500" size={16} />
                    <span>{new Date(video.created_at).toLocaleDateString()}</span>
                  </div>
                  <div className="flex items-center mb-4 text-gray-600">
                    <Clock className="mr-2 text-green-500" size={16} />
                    <span>{video.duration || 'Unknown duration'}</span>
                  </div>
                  <Link 
                    to={`/play/${video.id}`} 
                    className="flex items-center justify-center w-full bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
                  >
                    <PlayCircle className="mr-2" /> Play Video
                  </Link>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="bg-white shadow-lg rounded-lg p-6">
        <h3 className="text-xl font-semibold mb-4 text-gray-800">Dashboard Statistics</h3>
        <div className="grid grid-cols-3 gap-4">
          <div className="bg-blue-50 p-4 rounded-lg">
            <h4 className="text-sm text-gray-600">Total Videos</h4>
            <p className="text-2xl font-bold text-blue-600">{videos.length}</p>
          </div>
          <div className="bg-green-50 p-4 rounded-lg">
            <h4 className="text-sm text-gray-600">Total Recording Time</h4>
            <p className="text-2xl font-bold text-green-600">
              {videos.reduce((total, video) => {
                const duration = parseFloat(video.duration || 0);
                return total + duration;
              }, 0).toFixed(2)} mins
            </p>
          </div>
          <div className="bg-purple-50 p-4 rounded-lg">
            <h4 className="text-sm text-gray-600">Average Duration</h4>
            <p className="text-2xl font-bold text-purple-600">
              {videos.length > 0 
                ? (videos.reduce((total, video) => 
                    total + parseFloat(video.duration || 0), 0) / videos.length
                  ).toFixed(2)
                : 0} mins
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
