import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Video, Plus } from 'lucide-react';

function Dashboard() {
  const [videos, setVideos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch('http://localhost:5000/videos');
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  const handleVideoSelect = (filename) => {
    navigate('/play', { state: { filename } });
  };

  const handleNewRecording = () => {
    navigate('/record');
  };

  return (
    <div className="container mx-auto max-w-4xl px-4">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-800">My Video Library</h1>
        <button 
          onClick={handleNewRecording}
          className="flex items-center bg-blue-600 text-white px-4 py-2 rounded-lg shadow-md hover:bg-blue-700 transition duration-300"
        >
          <Plus className="mr-2" />
          New Recording
        </button>
      </div>

      {videos.length === 0 ? (
        <div className="text-center bg-white p-8 rounded-lg shadow-md border border-gray-200">
          <Video className="mx-auto mb-4 text-gray-400" size={64} />
          <p className="text-gray-600 text-lg">No videos recorded yet</p>
          <p className="text-gray-500 mb-4">Start by clicking "New Recording"</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {videos.map((video) => (
            <div 
              key={video.filename} 
              onClick={() => handleVideoSelect(video.filename)}
              className="bg-white rounded-lg shadow-md overflow-hidden cursor-pointer 
                         transform transition duration-300 hover:scale-105 hover:shadow-xl"
            >
              <div className="p-4">
                <div className="flex items-center justify-between">
                  <Video className="text-blue-600 mr-3" size={24} />
                  <span className="text-sm text-gray-500">
                    {(video.size / 1024).toFixed(2)} KB
                  </span>
                </div>
                <h3 className="mt-2 text-md font-semibold text-gray-800 truncate">
                  {video.filename}
                </h3>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
