// frontend/src/components/VideoRecorder.js
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { 
  Video, 
  Play, 
  Pause, 
  Square, 
  RadioTower, 
  CheckCircle2, 
  XCircle 
} from 'lucide-react';

function VideoRecorder() {
  const [recording, setRecording] = useState(false);
  const [stream, setStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedBlob, setRecordedBlob] = useState(null);
  const [recordingTime, setRecordingTime] = useState(0);
  const [error, setError] = useState(null);

  const videoRef = useRef(null);
  const chunksRef = useRef([]);
  const timerRef = useRef(null);

  useEffect(() => {
    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [stream]);

  const startRecording = async () => {
    try {
      const mediaStream = await navigator.mediaDevices.getUserMedia({ 
        video: { width: 1280, height: 720 }, 
        audio: true 
      });
      
      videoRef.current.srcObject = mediaStream;
      setStream(mediaStream);

      const recorder = new MediaRecorder(mediaStream, {
        mimeType: 'video/webm'
      });

      recorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunksRef.current.push(e.data);
        }
      };

      recorder.onstop = () => {
        const blob = new Blob(chunksRef.current, { type: 'video/webm' });
        setRecordedBlob(blob);
        chunksRef.current = [];
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
      setRecordingTime(0);

      timerRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);

    } catch (err) {
      setError('Failed to access camera/microphone');
      console.error(err);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      stream.getTracks().forEach(track => track.stop());
      clearInterval(timerRef.current);
      setRecording(false);
    }
  };

  const saveRecording = async () => {
    if (recordedBlob) {
      const formData = new FormData();
      formData.append('video', recordedBlob, 'recording.webm');
      formData.append('name', `Recording-${new Date().toISOString()}.webm`);

      try {
        await axios.post('http://localhost:5000/record', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        alert('Video saved successfully!');
        setRecordedBlob(null);
      } catch (error) {
        console.error('Error saving video:', error);
        setError('Failed to save video');
      }
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="container mx-auto p-6 bg-gray-50 min-h-screen flex flex-col items-center">
      <div className="w-full max-w-2xl bg-white shadow-lg rounded-lg p-6">
        <h1 className="text-3xl font-bold mb-6 text-gray-800 flex items-center">
          <Video className="mr-3 text-blue-600" size={36} /> 
          Video Recorder
        </h1>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
            <XCircle className="inline-block mr-2" />
            {error}
          </div>
        )}

        <div className="mb-6 border-2 border-gray-200 rounded-lg overflow-hidden">
          <video 
            ref={videoRef} 
            autoPlay 
            muted 
            className="w-full h-auto"
          />
        </div>

        <div className="flex items-center justify-center space-x-4 mb-6">
          {!recording ? (
            <button 
              onClick={startRecording} 
              className="flex items-center bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition"
            >
              <RadioTower className="mr-2" /> Start Recording
            </button>
          ) : (
            <button 
              onClick={stopRecording} 
              className="flex items-center bg-red-600 text-white px-6 py-3 rounded-lg hover:bg-red-700 transition"
            >
              <Square className="mr-2" /> Stop Recording
            </button>
          )}
        </div>

        {recording && (
          <div className="text-center mb-6">
            <span className="text-2xl font-bold text-red-600">
              {formatTime(recordingTime)}
            </span>
          </div>
        )}

        {recordedBlob && (
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 flex justify-between items-center">
            <div className="flex items-center">
              <CheckCircle2 className="text-green-600 mr-3" />
              <span>Recording ready to save</span>
            </div>
            <button 
              onClick={saveRecording}
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
            >
              Save Recording
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default VideoRecorder;
