// frontend/src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, NavLink } from 'react-router-dom';
import { 
  Monitor, 
  Video, 
  LayoutDashboard, 
  FileVideo 
} from 'lucide-react';
import Dashboard from './components/Dashboard';
import VideoRecorder from './components/VideoRecorder';
import VideoPlayer from './components/VideoPlayer';

function App() {
  return (
    <Router>
      <div className="flex min-h-screen bg-gray-100">
        {/* Sidebar */}
        <aside className="w-64 bg-white shadow-lg p-6">
          <div className="flex items-center mb-8">
            <Monitor className="mr-3 text-blue-600" size={32} />
            <h1 className="text-2xl font-bold text-gray-800">Video App</h1>
          </div>
          
          <nav>
            <ul className="space-y-2">
            <li>
                <NavLink 
                  to="/" 
                  className={({ isActive }) => `
                    flex items-center px-4 py-2 rounded-lg transition
                    ${isActive 
                      ? 'bg-blue-50 text-blue-600 font-semibold' 
                      : 'text-gray-600 hover:bg-gray-100'}
                  `}
                >
                  <FileVideo className="mr-3" size={20} />
                  Record Video
                </NavLink>
              </li>
              <li>
                <NavLink 
                  to="/dashboard" 
                  className={({ isActive }) => `
                    flex items-center px-4 py-2 rounded-lg transition
                    ${isActive 
                      ? 'bg-blue-50 text-blue-600 font-semibold' 
                      : 'text-gray-600 hover:bg-gray-100'}
                  `}
                >
                  <LayoutDashboard className="mr-3" size={20} />
                  Dashboard
                </NavLink>
              </li>
              
            </ul>
          </nav>
        </aside>

        {/* Main Content Area */}
        <main className="flex-grow">
          {/* Empty Navbar */}
          <nav className="bg-white shadow-sm p-4"></nav>

          <Routes>
            <Route path="/" element={<VideoRecorder />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/play/:videoId" element={<VideoPlayer />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
