// frontend/src/components/VideoPlayer.js
import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function VideoPlayer() {
  const { videoId } = useParams();
  const videoRef = useRef(null);
  const [videoSrc, setVideoSrc] = useState('');
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const fetchVideo = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/video/${videoId}`, {
          responseType: 'blob'
        });
        const videoBlob = new Blob([response.data], { type: 'video/webm' });
        setVideoSrc(URL.createObjectURL(videoBlob));
      } catch (error) {
        console.error('Error fetching video:', error);
      }
    };

    fetchVideo();
  }, [videoId]);

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (videoRef.current.paused) {
        videoRef.current.play();
        setIsPlaying(true);
      } else {
        videoRef.current.pause();
        setIsPlaying(false);
      }
    }
  };

  return (
    <div className="container mx-auto text-center">
      <h1 className="text-2xl font-bold mb-4">Video Player</h1>
      <video 
        ref={videoRef} 
        src={videoSrc} 
        controls
        className="mx-auto max-w-2xl border-2 border-gray-300"
      />
      <div className="mt-4 space-x-4">
        <button 
          onClick={togglePlayPause}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          {isPlaying ? 'Pause' : 'Play'}
        </button>
      </div>
    </div>
  );
}

export default VideoPlayer;
