import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Dashboard() {
  const [videos, setVideos] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchVideos();
  }, []);

  const fetchVideos = async () => {
    try {
      const response = await fetch('http://localhost:5000/videos');
      const data = await response.json();
      setVideos(data);
    } catch (error) {
      console.error('Error fetching videos:', error);
    }
  };

  const handleVideoSelect = (filename) => {
    navigate('/play', { state: { filename } });
  };

  return (
    <div className="container mx-auto">
      <h1 className="text-2xl font-bold mb-4">Video Dashboard</h1>
      {videos.length === 0 ? (
        <p className="text-gray-600">No videos recorded yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {videos.map((video) => (
            <div 
              key={video.filename} 
              className="bg-white p-4 rounded shadow cursor-pointer hover:bg-blue-50"
              onClick={() => handleVideoSelect(video.filename)}
            >
              <p className="font-medium">{video.filename}</p>
              <p className="text-sm text-gray-500">
                Size: {(video.size / 1024).toFixed(2)} KB
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
